import React, { useState } from "react";

const sampleProducts = [
  { id: 1, name: "Produto 1", price: 50 },
  { id: 2, name: "Produto 2", price: 75 },
  { id: 3, name: "Produto 3", price: 100 },
  { id: 4, name: "Produto 4", price: 25 },
  { id: 5, name: "Produto 5", price: 200 },
  { id: 6, name: "Produto 6", price: 150 },
  { id: 7, name: "Produto 7", price: 300 },
  { id: 8, name: "Produto 8", price: 80 },
  { id: 9, name: "Produto 9", price: 60 },
  { id: 10, name: "Produto 10", price: 40 },
];

function Shop() {
  const [cart, setCart] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 4;

  const totalPages = Math.ceil(sampleProducts.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentItems = sampleProducts.slice(startIndex, endIndex);

  const handleAddToCart = (product) => {
    setCart((prevCart) => [...prevCart, product]);
  };

  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  const totalItems = cart.length;
  const totalPrice = cart.reduce((acc, item) => acc + item.price, 0);

  return (
    <div className="shop-container">
      <h1>Loja de Produtos</h1>
      <div className="cart-summary">
        <p>Itens no carrinho: {totalItems}</p>
        <p>Total: R$ {totalPrice.toFixed(2)}</p>
      </div>
      <div className="product-list">
        {currentItems.map((product) => (
          <div key={product.id} className="product-card">
            <h3>{product.name}</h3>
            <p>Preço: R$ {product.price.toFixed(2)}</p>
            <button onClick={() => handleAddToCart(product)}>Adicionar ao Carrinho</button>
          </div>
        ))}
      </div>
      <div className="pagination">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>
          Página {currentPage} de {totalPages}
        </span>
        <button
          onClick={() => handlePageChange(currentPage + 1)}
          disabled={currentPage === totalPages}
        >
          Próxima
        </button>
      </div>
    </div>
  );
}

export default Shop;
