import React, { useState, useEffect } from "react";
import { logout } from "../utils/auth";
import { useNavigate } from "react-router-dom";

const sampleData = [
  { id: 1, name: "Cliente 1", ativo: true },
  { id: 2, name: "Cliente 2", ativo: false },
  { id: 3, name: "Cliente 3", ativo: true },
  { id: 4, name: "Cliente 4", ativo: true },
  { id: 5, name: "Cliente 5", ativo: false },
  { id: 6, name: "Cliente 6", ativo: true },
  { id: 7, name: "Cliente 7", ativo: false },
  { id: 8, name: "Cliente 8", ativo: true },
  { id: 9, name: "Cliente 9", ativo: true },
  { id: 10, name: "Cliente 10", ativo: false },
  { id: 11, name: "Cliente 11", ativo: true },
  { id: 12, name: "Cliente 12", ativo: true },
];

function Dashboard() {
  const navigate = useNavigate();
  const [data, setData] = useState(sampleData);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 5;

  const handleLogout = async () => {
    await logout();
    navigate("/");
  };

  const totalPages = Math.ceil(data.length / itemsPerPage);
  const startIndex = (currentPage - 1) * itemsPerPage;
  const endIndex = startIndex + itemsPerPage;
  const currentItems = data.slice(startIndex, endIndex);

  const handlePageChange = (newPage) => {
    if (newPage > 0 && newPage <= totalPages) {
      setCurrentPage(newPage);
    }
  };

  return (
    <div className="dashboard-container">
      <h1>Dashboard</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Nome</th>
            <th>Ativo</th>
          </tr>
        </thead>
        <tbody>
          {currentItems.map((item) => (
            <tr key={item.id}>
              <td>{item.id}</td>
              <td>{item.name}</td>
              <td>{item.ativo ? "Sim" : "Não"}</td>
            </tr>
          ))}
        </tbody>
      </table>

      {/* Paginação */}
      <div className="pagination">
        <button onClick={() => handlePageChange(currentPage - 1)} disabled={currentPage === 1}>
          Anterior
        </button>
        <span>
          Página {currentPage} de {totalPages}
        </span>
        <button onClick={() => handlePageChange(currentPage + 1)} disabled={currentPage === totalPages}>
          Próxima
        </button>
      </div>

      <button onClick={handleLogout} className="logout-button">Logout</button>
    </div>
  );
}

export default Dashboard;
