import { getAuth, signInWithPopup, GoogleAuthProvider, GithubAuthProvider, signOut, createUserWithEmailAndPassword, updateProfile } from "firebase/auth";
import { initializeApp } from "firebase/app";

const firebaseConfig = {
  apiKey: "AIzaSyCG5Ipy3n97_Q0wuW3OIpRlFWKRorRh8Wg",
  authDomain: "n3desweb.firebaseapp.com",
  projectId: "n3desweb",
  storageBucket: "n3desweb.firebasestorage.app",
  messagingSenderId: "506352280001",
  appId: "1:506352280001:web:085c2d8997a3e2f19b4f40",
  measurementId: "G-K4TTM7E95W"
};
// Inicializar Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth();

// Login com Google
export const signInWithGoogle = async () => {
  const provider = new GoogleAuthProvider();
  return signInWithPopup(auth, provider);
};

// Login com GitHub
export const signInWithGithub = async () => {
  const provider = new GithubAuthProvider();
  return signInWithPopup(auth, provider);
};

// Login com e-mail e senha
export const loginWithEmail = async (email, password) => {
  return signInWithEmailAndPassword(auth, email, password);
};

// Logout
export const logout = async () => {
  return signOut(auth);
};
