import React, { useState, useEffect } from "react";
import { logout, getUserData } from "../utils/auth";
import { useNavigate } from "react-router-dom";

function Dashboard() {
  const [userData, setUserData] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const authUser = JSON.parse(localStorage.getItem("user"));
        if (!authUser) {
          navigate("/");
          return;
        }

        const userInfo = await getUserData(authUser.uid);
        setUserData(userInfo);
      } catch (err) {
        setError(err.message);
        console.error("Erro no Dashboard:", err.message);
      }
    };

    fetchUserData();
  }, [navigate]);

  const handleLogout = async () => {
    await logout();
    localStorage.removeItem("user");
    navigate("/");
  };

  if (error) {
    return <p>{error}</p>;
  }

  if (!userData) {
    return <p>Carregando...</p>;
  }

  return (
    <div className="dashboard-container">
      <h1>{userData.isAdmin ? "Admin Dashboard" : "Meu Perfil"}</h1>
      {userData.isAdmin ? (
        <button onClick={() => navigate("/manage-users")}>Gerenciar Usu√°rios</button>
      ) : (
        <p>Bem-vindo, {userData.name}</p>
      )}
      <button onClick={handleLogout}>Logout</button>
    </div>
  );
}
