import React, { useState, useEffect } from "react";
import { logout, getUserData } from "../utils/auth";
import { useNavigate } from "react-router-dom";
import { getFirestore, collection, getDocs } from "firebase/firestore";

function Dashboard() {
  const navigate = useNavigate();
  const [userData, setUserData] = useState(null);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const authUser = JSON.parse(localStorage.getItem("user"));
        if (!authUser) {
          navigate("/");
          return;
        }

        const data = await getUserData(authUser.uid);
        setUserData(data);

        if (data.isAdmin) {
          await fetchAllUsers(); // Admins podem ver todos os usuários
        } else {
          setUsers([{ id: authUser.uid, ...data }]); // Usuário comum vê apenas seus dados
        }
      } catch (error) {
        console.error("Erro ao carregar os dados do usuário:", error);
        navigate("/");
      } finally {
        setLoading(false);
      }
    };

    fetchUserData();
  }, [navigate]);

  const fetchAllUsers = async () => {
    const db = getFirestore();
    const querySnapshot = await getDocs(collection(db, "users"));
    const usersData = querySnapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setUsers(usersData);
  };

  const handleLogout = async () => {
    await logout();
    localStorage.removeItem("user");
    navigate("/");
  };

  if (loading) {
    return <p>Carregando...</p>;
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <h1>{userData?.isAdmin ? "Admin Dashboard" : "Meu Perfil"}</h1>
        <div className="dashboard-actions">
          <button onClick={() => navigate("/shop")}>Voltar para Loja</button>
          <button onClick={handleLogout}>Sair</button>
        </div>
      </div>
      <p>Bem-vindo, {userData?.name}!</p>

      {userData?.isAdmin && (
        <>
          <h2>Lista de Usuários</h2>
          <table>
            <thead>
              <tr>
                <th>ID</th>
                <th>Nome</th>
                <th>E-mail</th>
                <th>Administrador</th>
              </tr>
            </thead>
            <tbody>
              {users.map((user) => (
                <tr key={user.id}>
                  <td>{user.id}</td>
                  <td>{user.name}</td>
                  <td>{user.email}</td>
                  <td>{user.isAdmin ? "Sim" : "Não"}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </>
      )}
    </div>
  );
}

export default Dashboard;
