import {
  getAuth,
  signInWithPopup,
  GoogleAuthProvider,
  GithubAuthProvider,
  signInWithEmailAndPassword,
  createUserWithEmailAndPassword,
  updateProfile,
  signOut,
} from "firebase/auth";
import { initializeApp } from "firebase/app";
import { getFirestore, doc, setDoc, getDoc } from "firebase/firestore";

const firebaseConfig = {
  apiKey: "AIzaSyCG5Ipy3n97_Q0wuW3OIpRlFWKRorRh8Wg",
  authDomain: "n3desweb.firebaseapp.com",
  projectId: "n3desweb",
  storageBucket: "n3desweb.appspot.com",
  messagingSenderId: "506352280001",
  appId: "1:506352280001:web:085c2d8997a3e2f19b4f40",
  measurementId: "G-K4TTM7E95W",
};

const app = initializeApp(firebaseConfig);
const auth = getAuth();
const db = getFirestore(app);

export const signInWithGoogle = async () => {
  const provider = new GoogleAuthProvider();
  const result = await signInWithPopup(auth, provider);
  const user = result.user;

  const userDoc = await getDoc(doc(db, "users", user.uid));
  if (!userDoc.exists()) {
    await setDoc(doc(db, "users", user.uid), {
      name: user.displayName,
      email: user.email,
      isAdmin: false, // Define padrão como usuário comum
    });
  }

  return user;
};

export const loginWithEmail = async (email, password) => {
  const userCredential = await signInWithEmailAndPassword(auth, email, password);
  return userCredential.user;
};

export const registerWithEmailAndPassword = async (name, email, password, isAdmin = false) => {
  const userCredential = await createUserWithEmailAndPassword(auth, email, password);
  const user = userCredential.user;

  await updateProfile(user, { displayName: name });

  await setDoc(doc(db, "users", user.uid), {
    name,
    email,
    isAdmin,
  });

  return user;
};

export const getUserData = async (uid) => {
  const userDoc = await getDoc(doc(db, "users", uid));
  return userDoc.exists() ? userDoc.data() : null;
};

export const logout = async () => {
  return signOut(auth);
};
